(8089, 720, '_form', '<div class=\"row\">\n	<div class=\"col-md-6 col-sm-12\">\n		<h4>Contact Information</h4>\n		<div class=\"box\">\n			[text* first_name class:form-control placeholder \"First Name*\"]\n		</div>\n		<div class=\"box\">\n			[text* last_name class:form-control placeholder \"Last Name*\"]\n		</div>\n		<div class=\"box\">\n			[email* email class:form-control placeholder \"Email*\"]\n		</div>\n		<div class=\"box\">\n			[text* phone_number class:form-control placeholder \"Phone number*\"]\n		</div>	\n		<div class=\"box\">\n			[text company class:form-control placeholder \"Company (if applicable)\"]\n		</div>	\n		<div class=\"box\">\n			[text referred class:form-control placeholder \"Referred by (if applicable)\"]\n		</div>									\n	</div>\n	<div class=\"col-md-6 col-sm-12\">\n		<h4>Property Name/Address</h4>\n		<div class=\"box\">\n			[text* street_addr class:form-control placeholder \"Street Address*\"]\n		</div>\n		<div class=\"box\">\n			[text street_addr2 class:form-control placeholder \"Address Line 2\"]\n		</div>\n		<div class=\"box\">\n			[text* city class:form-control placeholder \"City*\"]\n		</div>\n		<div class=\"box\">\n			[text* state class:form-control placeholder \"State*\"]\n		</div>	\n		<div class=\"box\">\n			[text* pin_code class:form-control placeholder \"Postal / Zip Code*\"]\n		</div>\n		<div class=\"box\">\n			[text* property_name class:form-control placeholder \"Property / Entity Name*\"]\n		</div>																			\n	</div>	\n	<div class=\"col-md-12 col-sm-12\">\n		<div class=\"btm-section\">\n			<div class=\"time-line\">\n				<h3>TIMELINE:</h3>\n				<p>Date you need study completed by: </p>\n			</div>\n			<div class=\"about-text\">\n				<h3>About subject project</h3>\n				<p>Building Usage </p>\n			</div>\n			<div class=\"row\">\n				<div class=\"col-md-4 col-sm-12\">\n					<div class=\"box\">\n						<label>DATE ACQUIRED / PLACED IN SERVICE *</label><div class=\"dt-box\">\n[text days class:field placeholder \"DD\"]\n[text months class:field placeholder \"MM\"]\n[text years class:field placeholder \"YYYY\"]\n						</div>\n					</div>\n				</div>\n				<div class=\"col-md-4 col-sm-12\">\n					<div class=\"box\">\n						<label></label>\n						[text* land_value class:form-control placeholder \"Land Value*\"]\n					</div>\n				</div>\n				<div class=\"col-md-4 col-sm-12\">\n					<div class=\"box\">\n						<label></label>\n						[text* square_feet_site class:form-control placeholder \"Square Feet Site*\"]\n					</div>\n				</div>\n				<div class=\"col-md-6 col-sm-12\">\n					<div class=\"box\">\n						<label></label>\n						[text* purchase_cost class:form-control placeholder \"Purchase Price or Construction Cost (excluding land)*\"]\n					</div>\n				</div>\n				<div class=\"col-md-6 col-sm-12\">\n					<div class=\"box\">\n						<label></label>\n						[text* square_feet_building class:form-control placeholder \"Square Feet Building(s)*\"]\n					</div>\n				</div>\n				<div class=\"col-md-12 col-sm-12\">\n					<div class=\"radio_panel\">\n						<h5>Number of Floors*</h5>\n						<p>If new construction or retrofit, does the building include energy efficient lighting and/or systems.</p>\n						\n							[radio number_of_floors label_first  use_label_element default:1 \"Yes\" \"No\"]\n						\n					</div>\n				</div>\n				<div class=\"col-md-12 col-sm-12\">\n					<div class=\"specify\">\n						<div class=\"row\">\n							<div class=\"col-lg-2 col-md-3 col-sm-12\">\n								<div class=\"sub-btn\">\n									[submit class:send_btn \"send\"]\n								</div>\n							</div>\n							<div class=\"col-lg-10 col-md-9 col-sm-12\">\n								<p>Please specify and additional Information on the property such as additional planned or past improvements (amount/year) or energy efficient building components:</p>\n							</div>\n						</div>\n					</div>\n				</div>\n			</div>\n		</div>\n	</div>			\n</div>'),
