(13014, 917, '_partial_deductions_title', 'field_61add3c1e13bb'),
(13015, 917, 'partial_deductions_content', '<p>If the 50% savings is not met, a partial deduction of up to $.60 per square foot is available for the cost of energy-saving improvements to any of the systems named above.</p>'),
(13016, 917, '_partial_deductions_content', 'field_61add3c9e13bc'),
(13017, 917, 'partial_deductions_data_table', '<thead>\r\n  <tr>\r\n    <th scope=\"col\">Size</th>\r\n    <th scope=\"col\">LED Lighting</th>\r\n    <th scope=\"col\">HVAC</th>\r\n    <th scope=\"col\">Envelope</th>\r\n    <th scope=\"col\">Max Deduction</th>\r\n  </tr>\r\n</thead>\r\n<tbody>\r\n  <tr>\r\n    <td>(in sf)</td>\r\n    <td>($0.30/sf to $0.60/sf)</td>\r\n    <td>($0.60/sf)</td>\r\n    <td>($0.60/sf)</td>\r\n    <td>($1.80/sf)</td>\r\n  </tr>\r\n  <tr>\r\n    <td>50K sf</td>\r\n    <td>$15K - $30K</td>\r\n    <td>$30K</td>\r\n    <td>$30K</td>\r\n    <td>$90K</td>\r\n  </tr>\r\n  <tr>\r\n    <td>300K sf</td>\r\n    <td>$90K - $180K</td>\r\n    <td>$180K</td>\r\n    <td>$180K</td>\r\n    <td>$540K</td>\r\n  </tr>\r\n  <tr>\r\n    <td>500K sf</td>\r\n    <td>$150K - $300K</td>\r\n    <td>$300K</td>\r\n    <td>$300K</td>\r\n    <td>$900K</td>\r\n  </tr>\r\n</tbody>'),
